import React from 'react';
import { useQuery, gql } from '@apollo/client';
import { List, Avatar, Skeleton, Anchor } from 'antd';

const CONTACTS = gql`
{
    contacts {
      email
      fname
      id
      lname
      twitter
    }
}
`;

export default function Planets(){
    const {loading, error, data } = useQuery(CONTACTS);
    if(loading) return <p>fetching data...</p>
    if(error) return console.error();
    return (
   <Anchor>
    <Link href="#components-anchor-demo-basic" title="Basic demo" />
    <Link href="#components-anchor-demo-static" title="Static demo" />
    <Link href="#components-anchor-demo-basic" title="Basic demo with Target" target="_blank" />
    <Link href="#API" title="API">
      <Link href="#Anchor-Props" title="Anchor Props" />
      <Link href="#Link-Props" title="Link Props" />
    </Link>
  </Anchor>,
    <List
    itemLayout="horizontal"
    dataSource={data.contacts}
    key={data.contacts.id}
    renderItem={item => (
        <List.Item
            actions={[<a key={item.id} href={`/contact/${item.id}`}>details</a>]}
          >
            <Skeleton avatar title={true} loading={item.loading} active>
              <List.Item.Meta
                avatar={
                  <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                }
                title={<a href={`/contact/${item.id}`}>{item.email}</a>}
                description={item.fname + " " + item.lname} 
              />
            </Skeleton>
          </List.Item>
    )}
   />
    );
}
