import React from 'react';
import { useQuery, gql } from '@apollo/client';
import { Form, Input, Button } from 'antd';

const Detail = gql`
query Contact($id: Int!) {
    contacts_by_pk(id: $id) {
      email
      fname
      id
      lname
      twitter
    }
  }
`;
const ADD_CONTACT = gql`
mutation ($email: String!, $fname: String!, $lname: String!) {
    AddContact(email: $email, fname: $fname, lname: $lname) {
      affected_rows
    }
  }
`;
const Details = ({
    match:{
        params: { id },
    },
}) => {
    const { loading, error, data } = useQuery(Detail, { variables: {id} });
    if(loading) return <p>fetching data...</p>
    if(error) return console.error();
    const [form] = Form.useForm();
    const layout = {
        labelCol: { span: 8 },
        wrapperCol: { span: 16 },
      };
      const tailLayout = {
        wrapperCol: { offset: 8, span: 16 },
      };
     const onReset = () => {
        form.resetFields();
     };
     const [addContact] = useMutation(ADD_CONTACT);
     const onFinish = values => {
        addContact({ variables: {fname: values.user.fname, lname: values.user.lname, email: values.user.email }})
        .then(() => setInputVal(""))
            .catch((e) => {
              setInputVal(e.message);
               onReset();
            });
    };
    const onFinishFailed = errorInfo => {
        console.log('Failed:', errorInfo);
    };
    const { email, fname, lname, twitter } = data.contacts_by_pk;
    return (
        <Form
        {...layout}
        name="basic"
        form={form}
        onFinish={onFinish}
        onFinishFailed={onFinishFailed}
        initialValues={{ remember: true }}
      >
        <Form.Item
          label="First Name"
          name={['user', 'fname']} 
          key={1}
          rules={[{ required: true, message: 'Please input your first name!' }]}
        ><Input placeholder="Contact First Name"/>
         </Form.Item>
        <Form.Item 
        label="Last Name"
        name={['user', 'lname']}
        rules={[{ required: true, message: 'Please input your last name!' }]}>
         <Input placeholder="Contact Last Name"/>
        </Form.Item>
        <Form.Item 
        name={['user', 'email']} 
        label="Email" 
        rules={[{ type: 'email',required: true, message: 'Please input your last email!' }]}>
         <Input />
        </Form.Item>
         <Form.Item
          label="Twitter"
          name={['user', 'twitter']} 
          key={4}
        ><Input />
         </Form.Item>
         <Form.Item {...tailLayout}>
         <Button type="primary" htmlType="submit">
           Submit
         </Button>
       </Form.Item>
      </Form>
    )
};

export default Details;
