import React from 'react';
import { useQuery, gql } from '@apollo/client';
import { List, Avatar, Skeleton } from 'antd';

const CONTACTS = gql`
{
    contacts {
      email
      fname
      id
      lname
      twitter
    }
}
`;

export default function Planets(){
    data:null;
    const {loading, error, data } = useQuery(CONTACTS);
    if(loading) return <p>loading...</p>
    if(error) return console.error()
    return data.contacts.map(({id, email, fname, lname, twitter }) => (
    <List
    itemLayout="horizontal"
    dataSource={data.contacts}
    key={data.contacts.id}
    renderItem={item => (
        <List.Item
            actions={[<a key={item.id} href={`/contact/${id}`}>details</a>]}
          >
            <Skeleton avatar title={false} loading={item.loading} active>
              <List.Item.Meta
                avatar={
                  <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                }
                title={<a href={`/contact/${id}`}>{item.email}</a>}
                description={fname + " " + lname} 
              />
            </Skeleton>
          </List.Item>
    )}
   />
    ));
}
